class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int flips = 0;
        for(int i = 0; i < m/2; i++)
        {
            for(int j = 0; j < n/2; j++)
            {
                int currOnes = grid[i][j] + grid[i][n - j - 1] + grid[m - i - 1][j] + grid[m - i - 1][n - j - 1];
                if(currOnes == 1 || currOnes == 3)
                {
                    flips++;
                }
                else if(currOnes == 2)
                {
                    flips += 2;
                }
            }
        }
        if(m%2 == 1 && n%2 == 1 && grid[m/2][n/2] == 1)
        {
            flips++;
        }
        int ones = 0, zeros = 0, diff = 0;
        if(m%2 == 1)
        {
            for(int i = 0; i < n/2; i++)
            {
                int currOnes = grid[m/2][i] + grid[m/2][n - i - 1];
                if(currOnes == 0)
                {
                    zeros++;
                }
                else if(currOnes == 2)
                {
                    ones++;
                }
                else
                {
                    diff++;
                }
            }
        }
        if(n%2 == 1)
        {
            for(int i = 0; i < m/2; i++)
            {
                int currOnes = grid[i][n/2] + grid[m - i - 1][n/2];
                if(currOnes == 0)
                {
                    zeros++;
                }
                else if(currOnes == 2)
                {
                    ones++;
                }
                else
                {
                    diff++;
                }
            }
        }
        flips += diff;
        if(diff == 0 && ones%2 == 1)
        {
            flips += 2;
        }
        return flips;
    }
};
