class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size(),m=g[0].size();

        int ct=0,ct2=0;
        vector<int> a(4,0),b(3,0);

        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int x=g[i][j]+g[n-1-i][j]+g[n-1-i][m-1-j]+g[i][m-1-j];
                if(x==0 || x==4) continue;
                ct+=x;
                a[x]++;
            }
        }

        if(n&1){
            for(int j=0;j<m/2;j++){
                int x=g[n/2][j]+g[n/2][m-1-j];
                b[x]++;
                ct2+=x;
            }
        }

        int y=0;

        if(m&1){
            for(int j=0;j<n/2;j++){
                int x=g[j][m/2]+g[n-1-j][m/2];
                b[x]++;
                ct2+=x;
            }

            if(n&1) y=g[n/2][m/2];
        }

        int ans=a[1]+a[3]+2*a[2]+b[1]+y;
        ct=2*b[2];
        if(ct%4==0) return ans;
        if(ct%4==2 && ( b[1]>0)) return ans;
         return ans+2;
    }
};
