class Solution {
    public int minFlips(int[][] grid) {
        int s0 = 0;
        int s1 = 0;

        int d0 = 0;
        int d1 = 0;
        int d01 = 0;

        int res = 0;

        int m = grid.length, n = grid[0].length;

        for (int r = 0; r < (m + 1) / 2; ++r) {
            for (int c = 0; c < (n + 1) / 2; ++c) {
                int o = grid[r][c];

                int r1 = m - 1 - r;
                int c1 = n - 1 - c;

                int h = grid[r1][c];
                int v = grid[r][c1];
                int d = grid[r1][c1];

                if (r == r1 && c == c1) {
                    if (o == 0) {
                        ++s0;
                    } else {
                        ++s1;
                    }
                } else if (r == r1) {
                    if (o == v) {
                        if (o == 0) {
                            ++d0;
                        } else {
                            ++d1;
                        }
                    } else {
                        ++d01;
                    }
                } else if (c == c1) {
                    if (o == h) {
                        if (o == 0) {
                            ++d0;
                        } else {
                            ++d1;
                        }
                    } else {
                        ++d01;
                    }
                } else {
                    int[] cnt = new int[2];
                    ++cnt[o];
                    ++cnt[h];
                    ++cnt[v];
                    ++cnt[d];
                    res += Math.min(cnt[0], cnt[1]);
                }
            }
        }

        res += s1 + d01;
        if (d1 % 2 != 0 && d01 == 0) {
            res += 2;
        }

        return res;
    }
}
