using vi = vector<int>;
using vvi = vector<vi>;
class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = (int)grid.size(), n = (int)grid[0].size();
        int ans = 0, cnt = 0, opt = 0;
        for (int i = 0;i<=(m - 1)/2;i++){
            for (int j = 0;j<=(n - 1)/2;j++){
                map<int,int>vals;
                vals[grid[i][j]]++;
                vals[grid[m - i - 1][j]]++;
                vals[grid[i][n - j - 1]]++;
                vals[grid[m - i - 1][n - j - 1]]++;
                if (i != m - i - 1 and j != n - j - 1)
                    ans += min(4 - vals[0], 4 - vals[1]);
                else if (i == m - i - 1 and j == n - j - 1)
                    cnt += grid[i][j];
                else if (i == m - i - 1){
                    if (grid[i][j] == grid[i][n - j - 1])cnt += 2 * grid[i][j];
                    else ans++, opt++;
                }
                else{
                    if (grid[i][j] == grid[m - i - 1][j])cnt += 2 * grid[i][j];
                    else ans++, opt++;
                }
            }
        }
        if (cnt%4 == 1)ans++;
        if (cnt%4 == 2 and opt == 0)ans += 2;
        if (cnt%4 == 3 and opt == 0)ans += 3;
        if (cnt%4 == 3 and opt != 0)ans++;
        return ans;
    }
};
