class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0, o = 0, t = 0;
        if((m%2 == 1) && (n%2 == 1)) o += grid[m/2][n/2];
        if(n % 2 != 0)
        {
            for(int i=0; i<m/2; i++)
            {
                if(grid[m-i-1][n/2] + grid[i][n/2] == 2) o += 2;
                else{
                    if(grid[m-i-1][n/2] + grid[i][n/2] ==1)
                    {
                        o += 2;
                        t++;
                    }
                    ans += ((grid[m-i-1][n/2] + grid[i][n/2]) % 2);   
                }
            }
        }
        if(m % 2 != 0)
        {
            for(int j=0; j<n/2; j++)
            {
                if(grid[m/2][j] + grid[m/2][n-j-1] == 2) o += 2;
                else
                {
                    if(grid[m/2][j] + grid[m/2][n-j-1] == 1)
                    {
                        o += 2;
                        t++;
                    }
                    ans += ((grid[m/2][j] + grid[m/2][n-j-1]) % 2);
                }
            }
        }
        for(int i=0; i<m/2; i++)
        {
            for(int j=0; j<n/2; j++)
            {
                if(grid[i][j] + grid[i][n-j-1] + grid[m-i-1][j] + grid[m-i-1][n-j-1] > 2) o += 4;
                if(grid[i][j] + grid[i][n-j-1] + grid[m-i-1][j] + grid[m-i-1][n-j-1] == 2) ans += 2;
                else ans += ((grid[i][j] + grid[i][n-j-1] + grid[m-i-1][j] + grid[m-i-1][n-j-1]) % 2);
            }
        }

        if(o % 2 != 0)
        {
            ans++;
            o--;
        }
        if(o % 4 == 0) return ans;
        return t>0 ? ans : ans + 2;
    }
};
