class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ct_one = 0;
        int n = grid.size();
        int m = grid[0].size();

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                ct_one = (grid[i][j] == 1) ? (ct_one + 1) : ct_one;
            }
        }

        if ((n * m) < 4) {
            return ct_one;
        }

        int ans = 0;

        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int ct = 0;
                if (grid[i][j] == 1) ct++;
                if (grid[i][m - j - 1] == 1) ct++;
                if (grid[n - i - 1][j] == 1) ct++;
                if (grid[n - i - 1][m - j - 1] == 1) ct++;
                ans += min(ct, 4 - ct);
            }
        }

        if (n % 2 == 0 && m % 2 == 0) {
            return ans;
        }

        int ct = 0;
        int no_of_cells = 0;
        int ct_1 = 0;

        if (m % 2 == 1) {
            for (int i = 0; i < n / 2; i++) {
                if (grid[i][m / 2] != grid[n - i - 1][m / 2]) {
                    ct++;
                } else {
                    if (grid[i][m / 2]) ct_1 += 2;
                }
            }
            no_of_cells += n;
        }

        if (n % 2 == 1) {
            for (int i = 0; i < m / 2; i++) {
                if (grid[n / 2][i] != grid[n / 2][m - i - 1]) {
                    ct++;
                } else {
                    if (grid[n / 2][i]) ct_1 += 2;
                }
            }
            no_of_cells += m;
        }

        ans += ct;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) ct_1++;
        }

        if (ct_1 % 4 == 0) return ans;
        if (ct_1 % 4 == 1) return (ans + 1);
        if (ct_1 % 4 == 2) {
            if (ct > 0) return ans;
            return (ans + 2);
        }
        if (ct_1 % 4 == 3) {
            if (ct > 0) return (ans + 1);
            return (ans + 3);
        }

        return ans;  
    }
};
