class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(), m=grid[0].size();
        int t=0, b=n-1, l=0, r=m-1, ans=0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int r=n-1-i, c=m-1-j;
                int sum=grid[i][j]+grid[i][c]+grid[r][j]+grid[r][c];
                if(sum==2) ans+=2;
                else if(sum==1 || sum==3) ans++;
                //cout<<i<<" "<<j<<" "<<ans<<" ";
            }
        }
        int o1=0, o2=0, oo1=0, oo2=0;
        if((n&1)){
            int l=0, r=m-1;
            while(l<r){
                if(grid[n/2][l]==1 && grid[n/2][r]==1) oo1++;
                if(grid[(n)/2][l]!=grid[(n)/2][r]) o1++;
                l++;
                r--;
            }
        }
        if((m&1)){
            int l=0, r=n-1;
            while(l<r){
                if(grid[l][m/2]==1 && grid[r][m/2]==1) oo2++;
                if(grid[l][(m)/2]!=grid[r][(m)/2]) o2++;
                l++;
                r--;
            }
        }
        if((oo1+oo2)%2==1 && o1==0 && o2==0) ans+=2;
        cout<<ans<<" "<<oo1<<" "<<oo2<<" ";
        if(n%2==0){
            if(m%2==0) return ans;
            else{
                return ans+o2;
            } 
        }
        else{
            if(m%2==0) return ans+o1;
            else{
                if(grid[(n)/2][(m)/2]==1) return ans+o1+o2+1;
                else return ans+o1+o2;
            }
        } 
    }
};
