class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int result=0;
        for(int i=0;i<m/2;i++)
        {
            for(int j=0;j<n/2;j++)
            {
                int ones=grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-i-1][n-j-1];
                // cout<<i<<" "<<j<<" "<<ones<<endl;
                result+=min(4-ones,ones);
            }
        }
        int choices=0;
        int extras=0;
        if(m%2==1)
        {
            for(int i=0;i<n/2;i++)
            {
                if(grid[m/2][i]!=grid[m/2][n-i-1]) choices++;
                else 
                {
                    if(grid[m/2][i]==1) extras++;
                }
            }
        }
        if(n%2==1)
        {
            for(int i=0;i<m/2;i++)
            {
                if(grid[i][n/2]!=grid[m-i-1][n/2]) choices++;
                else
                {
                    if(grid[i][n/2]==1) extras++;
                }
            }
        }
        if(m%2==1 && n%2==1)
        {
            if(grid[m/2][n/2]==1)
            {
                result++;
            }
        }
        // cout<<result<<" "<<extras<<" "<<choices<<endl;
        int total_ones=2*extras+choices;
        if(total_ones%4==0) return result+choices;
        else if(total_ones%4==1)
        {
            if(choices) return result+choices;
        }
        else if(total_ones%4==2)
        {
            if(choices) return result+choices;
            else return result+2;//check
        }
        else
        {
            if(choices) return result+choices;
        }
        return result;
    }
};
