class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int curr = grid[i][j];
                int right = grid[i][m-j-1];
                int down = grid[n-i-1][j];
                int diag = grid[n-i-1][m-j-1];
                int ones = curr+right+down+diag;
                ans += min(ones,4-ones);
            }
        }
        int ones = 0,extra=0;
        if(n&1){
            int s = 0,e=m-1;
            while(s<e){
                ones += (grid[n/2][s] && grid[n/2][e]) ? 2 : 0;
                if(grid[n/2][s]!=grid[n/2][e]){
                    extra++;
                }
                s++;
                e--;
            }
        }
        if(m&1){
            int s = 0,e=n-1;
            while(s<e){
                ones += (grid[s][m/2] && grid[e][m/2]) ? 2 : 0;
                if(grid[s][m/2]!=grid[e][m/2]){
                    extra++;
                }
                s++;
                e--;
            }
        }
        if(m&1 && n&1) ans+= grid[n/2][m/2];
        int x = ones%4;
        if(x==2 && extra>=1) ones=0;

        return ans+extra+(ones%4);
    }
};
