    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int n1 = n / 2, m1 = m / 2, ans = 0, s = 0;
        for(int i = 0; i < n1; ++i){
            for(int j = 0; j < m1; ++j){
                int c = 0;
                c += grid[i][j];
                c += grid[i][m - j - 1];
                c += grid[n - i - 1][j];
                c += grid[n - i - 1][m - j - 1];
                //优先选择操作次数少的方案
                //默认将0变成1
                if(c == 2)ans += 2;
                else if(c == 3)ans++;
                else if(c == 1)ans++;      
            }
        }
