class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int m = grid.size();
        int n = grid[0].size();
        int res = 0;
        int totsum = 0;
        for (int i = 0 ; i <m; i++) {
            for (int j = 0;  j < n; j++) {
                totsum += grid[i][j];
            }
        }

        for (int i = 0; i < m / 2; i++) {
            for (int  j = 0; j < n / 2; j++) {
                int sum = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1- j] +grid[m-1-i][n-1-j];
                if (sum > 0) {
                    totsum += 4 - sum;
                }
                if (sum % 2 == 1) {
                    res += 1;
                } else if (sum % 4 == 2) {
                    res += 2;
                }
            }
        }
        int res2= 0;
        if (m % 2== 1) {
            for (int j = 0; j < n / 2; j++) {
                if (grid[m / 2][j] != grid[m/2][n - 1-j]) {
                    res2++;
                }
            }
        }
        if (n % 2== 1) {
            for (int i = 0; i <m/ 2; i++) {
                if (grid[m - 1 -i][n / 2] != grid[i][n /2]) {
                    res2++;
                }
            }
        }

        if (totsum % 4 == 1 || totsum % 4 ==3) {
            if (res2 == 0) {
                return res + totsum % 4;
            }
            if (res2 % 2 == 0) {
                return res+res2+1;
            }
        }
        if (totsum % 4 == 0  && res2 % 2 == 1) {
            return res + res2+1;
        }
        if (totsum % 4 == 2) {
            if (res2 == 0) {
                return res + 2;
            }
            if (res2 % 2 == 1) {
                return res+res2 +1;
            }
        }
        return res+res2;
    }
};
