class Solution {
     int check(string s){
        int i =0;
        int j = s.size()-1;
        int ans=0;
        while(i<=j){
            if(s[i]!=s[j]){
                ans++;
            }
            i++;
            j--;
        }
        return ans;
    }
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<vector<int>>matrix = grid;
        int rows = matrix.size(), cols = matrix[0].size();
        int totalFlips = 0;

        // Check the central element in case of odd dimensions
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (matrix[rows / 2][cols / 2] == 1) {
                totalFlips += 1;
            }
        }

        vector<int> flipCount(3, 0);

        // Process the middle row for odd number of rows
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = matrix[rows / 2][left] + matrix[rows / 2][right];
                flipCount[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        // Process the middle column for odd number of columns
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = matrix[top][cols / 2] + matrix[bottom][cols / 2];
                flipCount[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        // Adjust flips for middle row/column
        if (flipCount[2] % 2 == 1) {
            if (flipCount[1] > 0) {
                totalFlips += flipCount[1];
            } else {
                totalFlips += 2;
            }
        } else {
            totalFlips += flipCount[1];
        }

        // Process the quadrants
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int a = matrix[i][j];
                int b = matrix[rows - 1 - i][j];
                int c = matrix[i][cols - 1 - j];
                int d = matrix[rows - 1 - i][cols - 1 - j];

                int sum = a + b + c + d;

                if (sum != 0 && sum != 4) {
                    totalFlips += min(sum, 4 - sum);
                }
            }
        }

        return totalFlips;
    }
};
