class Solution {
    public int minFlips(int[][] arr) {
        int ans=0;int m=arr[0].length,n=arr.length;
        for(int i=0;i<m/2;i++){
           int q=0,w=n-1,r=m-1-i,t=n-1;
           while(q<n/2){
            int c0=0,c1=0;
            if(arr[q][i]==1) c1++; else c0++;
            if(arr[n-1-q][i]==1) c1++; else c0++;
            if(arr[q][r]==1) c1++; else c0++;
            if(arr[n-1-q][r]==1) c1++; else c0++;
           // System.out.println(c0+" "+c1);
            q++;
            if(c1!=4 || c0!=4) ans+=Math.min(c0,c1);
           }
        }
       // System.out.println(ans);
        int ones=0,noteq=0;
        if(n%2==1){
            //int noteq=0,ones=0;
            for(int i=0;i<m/2;i++){
                if(arr[(n/2)][i]!=arr[(n/2)][m-1-i]) noteq++;
                if(arr[(n/2)][i]==1) ones+=2;
            }
        }
        if(m%2==1){
           // int noteq=0,ones=0;
            for(int i=0;i<n/2;i++){
                if(arr[i][m/2]!=arr[n-1-i][m/2]) noteq++;
                if(arr[i][m/2]==1) ones+=2;
            }
            //  System.out.println(ones);
        }
         if(ones%4==2){
                if(noteq==0) ans+=2;
                else ans+=noteq;
            }else ans+=noteq;
        if(n%2==1 && m%2==1 && arr[n/2][m/2]==1) ans++;
        return ans;
    }
}
