public class Solution {
    public int minFlips(int[][] grid) {
         int x = 0;

        int n = grid.length;
        int m = grid[0].length;
           int[] arr = new int[3];

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                x ++;
            }
        }



        if(m%2==1 || n%2 ==1){
          if (m % 2 == 1) {
            int start =0 ;
            int end = n - 1;
            while (start < end) {
                int sum = grid[start][m / 2] + grid[end][m / 2];
                arr[sum]++;
                start++;
                end--;
            }
        }

        if (n % 2 == 1) {
            int start = 0;
            int end = m - 1;
            while (start < end) {
                int sum = grid[n / 2][start] + grid[n / 2][end];
                arr[sum]++;
                start++;
                end--;
            }
        }
        }




        if (arr[2] % 2 == 1) {
            if (arr[1] > 0) {
                x += arr[1];
            } else {
                x += 2;
            }
        } else {
            x += arr[1];
        }


        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int firsum = grid[i][j];
                 int thirdseum = grid[i][m - 1 - j];
                int fourthsum = grid[n - 1 - i][m - 1 - j];
                int secsum = grid[n - 1 - i][j];


                int sum = firsum + secsum + thirdseum + fourthsum;
                if (sum != 0 && sum != 4) {
                    x += Math.min(sum, 4 - sum);
                }
            }
        }

        return x;
    }


}
