class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0, n = grid.size(), m = grid[0].size();
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int ones = grid[i][j] + grid[i][m - j - 1] + 
                            grid[n - i - 1][j] + grid[n - i - 1][m - j - 1];
                ans += min(ones, 4 - ones);
            }
        }

        int pairs = 0, dif = 0, ch = 0;
        if (m % 2) {
            for (int i = 0; i < n / 2; ++i) {
                ch += grid[i][m / 2] != grid[n - i - 1][m / 2];
                if (grid[i][m / 2] == grid[n - i - 1][m / 2] && grid[i][m / 2] == 1) {
                    pairs++;
                } else {
                    dif++;
                }
            }
        }

        if (n % 2) {
            for (int i = 0; i < m / 2; ++i) {
                ch += grid[n / 2][i] != grid[n / 2][m - i - 1];
                if (grid[n / 2][i] == grid[n / 2][m - i - 1] && grid[n / 2][i] == 1) {
                    pairs++;
                } else {
                    dif++;
                }
            }
        }

        if (n % 2 && m % 2) ans += grid[n / 2][m / 2];

        if (pairs % 2 == 0) {
            // cout << "case1\n";
            cout << ans << " " << ch << endl;
            return ans + ch;
        } else if (ch >= 2) {
            // cout << "case2\n";
            return ans + ch;
        } else if (ch == 1) {
            // cout << "case3\n";
            return ans + ch;
        } else {
            // cout << "case4\n";
            return ans + ch + 2;
        }
    }
};
