class Solution {
public:
    typedef pair<int, int> ii;
    ii rec(int x, int y, vector<vector<int>> &grid, vector<vector<int>> &vis) {
        int m = grid.size(), n = grid[0].size();
        set<ii> st;
        st.insert({x, y});
        int sum = grid[x][y];
        if (st.count({x, n - 1 - y}) == 0) {
            sum += grid[x][n - 1 - y];
            st.insert({x, n - 1 - y});
        }
        if (st.count({m - 1 - x, y}) == 0) {
            sum += grid[m - 1 - x][y];
            st.insert({m - 1 - x, y});
        }
        if (st.count({m - 1 - x, n - 1 - y}) == 0) {
            sum += grid[m - 1 - x][n - 1 - y];
            st.insert({m - 1 - x, n - 1 - y});
        }
        for (auto [a, b]: st) {
            vis[a][b] = 1;
        }
        return {st.size(), sum};
    }
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        vector<vector<int>> vis(m, vector<int> (n, 0));
        int ans = 0;
        int tot1 = 0, tot2 = 0;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (vis[i][j]) {
                    continue;
                }
                ii x = rec(i, j, grid, vis);
                if (x.first == 2) {
                    if (x.second == 1) {
                        ans++;
                        tot1++;
                    }
                    if (x.second == 2) {
                        tot2++;
                    }
                }
                if (x.first == 4) {
                    if (x.second == 1 || x.second == 3) {
                        ans++;
                    }
                    if (x.second == 2) {
                        ans += 2;
                    }
                }
            }
        }
        if (tot2 % 2 == 1) {
            if (tot1 == 0) {
                ans += 2;
            }
        }
        if (m % 2 && n % 2) {
            if (grid[m / 2][n / 2] == 1) {
                ans++;
            }
        }
        return ans;
    }
};
