class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int bn=(n/2)-1,bm=(m/2)-1;
        int ans=0;
        for(int i=0;i<=bn;i++){
           for(int j=0;j<=bm;j++){
               int cnt1=(grid[i][j])+(grid[i][m-1-j])+(grid[n-1-i][j])+(grid[n-1-i][m-1-j]);
               ans+=(min(4-cnt1,cnt1));
           }
        }
        int opt_x=0,opt_y=0,movex=0,movey=0,cnt1x=0,cnt1y=0;
        //cout<<bn<<" "<<bm<<" "<<ans<<" ";
        if((n%2==1) && (m%2==1) && (grid[bn+1][bm+1])==1){
            ans++;
        }
        if((n%2)==1){
            int i=0,j=m-1;
            while(i<j){
                if(grid[bn+1][i]!=grid[bn+1][j]){
                    movex++;
                    cnt1x+=2;
                }
                else if(grid[bn+1][i]==grid[bn+1][j] && grid[bn+1][i]==1){
                    cnt1x+=2;
                }
                i++; j--;
            }
        }
       // cout<<cnt1x<<" "<<movex<<" , ";
        if((m%2)==1){
            int i=0,j=n-1;
            while(i<j){
                if(grid[i][bm+1]!=grid[j][bm+1]){
                    movey++;
                    cnt1y+=2;
                }
                else if(grid[i][bm+1]==grid[j][bm+1] && grid[i][bm+1]==1){
                    cnt1y+=2;
                }
                i++; j--;
            }
        }
            if((cnt1x+cnt1y)%4!=0){
                if(movex==0 && movey==0){
                    movex+=2;
                }
            }
            ans+=(movex+movey);
       // cout<<cnt1y<<" "<<movey<<" , "<<movex<<" "<<ans<<"\n";
        return ans;

    }
};
