func minFlips(grid [][]int) int {
    m, n := len(grid), len(grid[0])
    flips := 0
    for i := 0; i < m/2; i++ {
        for j := 0; j < n/2; j++ {
            diff := 0
            if i != m-1-i && grid[i][j] != grid[m-1-i][j] {
                diff++
            }
            if j != n-1-j && grid[i][j] != grid[i][n-1-j] {
                diff++
            }
            if i != m-1-i && j != n-1-j && grid[i][j] != grid[m-1-i][n-1-j] {
                diff++
            }

            flips += min(diff, 4-diff)
        }
    }

    if m%2 == 0 && n%2 == 0 {
        return flips
    }

    ones := 0
    cFlips := 0

    if m%2 == 1 {
        for i := 0; i < n/2; i++ {
            if grid[m/2][i] != grid[m/2][n-1-i] {
                cFlips += 1
                continue
            } 

            if grid[m/2][i] == 1 {
                ones += 2
            }
        }
    }

    if n%2 == 1 {
        for i := 0; i < m/2; i++ {
            if grid[i][n/2] != grid[m-1-i][n/2] {
                cFlips += 1
                continue
            } 

            if grid[i][n/2] == 1 {
                ones += 2
            }
        }
    }

    flips += cFlips
    if cFlips == 0 && ones%4 != 0 {
        flips += 2
    }

    if m%2 == 1 && n%2 == 1 {
        if grid[m/2][n/2] == 1 {
            flips++
        } 
    }

    return flips
}
