class Solution(object):
    def minFlips(self, grid):
        n = len(grid)
        m = len(grid[0])
        res = 0


        if n % 2 == 1 and m % 2 == 1:
            if grid[n // 2][m // 2] == 1:
                res += 1

        shift = [0] * 3

        if n % 2 == 1:
            mid_row = n // 2
            a, b = 0, m - 1
            while a < b:
                sm = grid[mid_row][a] + grid[mid_row][b]
                shift[sm] += 1
                a += 1
                b -= 1

        if m % 2 == 1:
            mid_col = m // 2
            a, b = 0, n - 1
            while a < b:
                sm = grid[a][mid_col] + grid[b][mid_col]
                shift[sm] += 1
                a += 1
                b -= 1

        if shift[2] % 2 == 1:
            res += shift[1] if shift[1] > 0 else 2
        else:
            res += shift[1]


        for i in range(n // 2):
            for j in range(m // 2):
                a = grid[i][j]
                b = grid[n - 1 - i][j]
                c = grid[i][m - 1 - j]
                d = grid[n - 1 - i][m - 1 - j]

                sm = a + b + c + d

                if sm != 0 and sm != 4:
                    res += min(sm, 4 - sm)

        return res
