class Solution {
    public int minFlips(int[][] g) {
        int m=g.length, n=g[0].length;

        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int trx=i, trry=j;
                int tlx=i, tly=n-j-1;
                int brx=m-i-1, bry=j;
                int blx=m-i-1, bly=n-j-1;

                int o=0, z=0;
                if(g[trx][trry]==0) z++;
                else o++;

                if(g[tlx][tly]==0) z++;
                else o++;

                if(g[brx][bry]==0) z++;
                else o++;

                if(g[blx][bly]==0) z++;
                else o++;

                ans+=Math.min(4-o, 4-z);
            }
        }

        int oc=0;
        boolean hadMM=false;
        if(m%2!=0){
            int l=0, r=n-1;
            while(l<r){
                if(g[m/2][l]!=g[m/2][r]){
                    ans++;
                    hadMM=true;
                }
                else if(g[m/2][l]==1) oc+=2;
                l++;r--;
            }
        }

        if(n%2!=0){
            int u=0, d=m-1;
            while(u<d){
                if(g[u][n/2]!=g[d][n/2]){
                    ans++;
                    hadMM=true;
                }
                else if(g[u][n/2]==1) oc+=2;
                u++;d--;
            }
        }

        if(n%2!=0 && m%2!=0 && g[m/2][n/2]==1) ans++;

        if(oc%4!=0 && !hadMM) ans+=2;
        return ans;        
    }
}
