class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int cnt = 0;

        for (int i = 0; i < m / 2; ++i) {
            for (int j = 0; j < n / 2; ++j) {
                int temp = 0;
                temp += grid[i][j];
                temp += grid[i][n - 1 - j];
                temp += grid[m - 1 - i][j];
                temp += grid[m - 1 - i][n - 1 - j];
                cnt += min(temp, 4 - temp);
            }
        }
        int sum1 = 0, diff1 = 0;
        if (m % 2) {
            for (int j = 0; j < n / 2; ++j) {
                if (grid[m / 2][j] != grid[m / 2][n - 1 - j]) diff1++;
                sum1 += grid[m / 2][j];
                sum1 += grid[m / 2][n - 1 - j];
            }
        }
        int sum2 = 0, diff2 = 0;
        if (n % 2) {
            for (int i = 0; i < m / 2; ++i) {
                if (grid[i][n / 2] != grid[m - 1 - i][n / 2]) diff2++;
                sum2 += grid[i][n / 2];
                sum2 += grid[m - 1 - i][n / 2];
            }

        }
        bool ok = false;
        for (int i = sum1 + sum2 - diff1 - diff2; i <= sum1 + sum2 + diff1 + diff2; i += 2) {
            if (i % 4 == 0) {
                ok = true;
                break;
            }
        }
        if (ok) cnt += diff1 + diff2;
        else cnt += (diff1 + diff2 + 2);
        if (m % 2 && n % 2 && grid[m / 2][n / 2] == 1) cnt++;
        return cnt;
    }
};
