class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows=grid.size();
        int cols=grid[0].size();
        int ans=0;

        for(int curR=0;curR<rows/2;curR++){
            for(int curC=0;curC<cols/2;curC++){
                int zeros=0;
                if(grid[curR][curC]==0){
                    zeros+=1;
                }
                if(grid[rows-1-curR][curC]==0){
                    zeros+=1;
                }
                if(grid[rows-1-curR][cols-1-curC]==0){
                    zeros+=1;
                }
                if(grid[curR][cols-1-curC]==0){
                    zeros+=1;
                }
                ans+=min(zeros,4-zeros);
            }
        }

        int alter=0,ones=0,zeros=0;

        if(rows%2!=0){
            int  curR=rows/2;
            for(int curC=0;curC<cols/2;curC++){
                if(grid[curR][cols-1-curC]==0 && grid[curR][curC]==0){
                    zeros+=1;
                }
                else if(grid[curR][cols-1-curC]==1 && grid[curR][curC]==1){
                    ones+=1;
                }
                else{
                    alter+=1;
                }
            }
        }

        if(cols%2!=0){
            int  curC=cols/2;
            for(int curR=0;curR<rows/2;curR++){
                if(grid[rows-1-curR][curC]==0 && grid[curR][curC]==0 ){
                    zeros+=1;
                }
                else if(grid[rows-1-curR][curC]==1 && grid[curR][curC]==1){
                    ones+=1;
                }else{
                    alter+=1;
                }
            }
        }


            int  curR=rows/2;
            int  curC=cols/2;
        if(rows%2!=0 && cols%2!=0 && grid[curR][curC]==1){

            if(ones%2==0 ){
                ans+=alter;
            }else if(ones%2==1 && alter>0) {
                ans+=alter;
            }else if(ones%2==1 && alter==0){
                ans+=alter+2;
            }
            ans+=1;

        }else{

            if(ones%2==0 ){
                ans+=alter;
            }else if(ones%2==1 && alter>0) {
                ans+=alter;
            }else if(ones%2==1 && alter==0){
                ans+=alter+2;
            }
        }

        return ans;
    }
};
