class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        H, W = len(grid), len(grid[0])
        ans = 0
        for y in range(H // 2):
            for x in range(W // 2):
                s = grid[y][x] + grid[H - 1 - y][x] + grid[y][W - 1 - x] + grid[H - 1 - y][W - 1 - x]
                ans += min(s, 4 - s)
        ans0 = 0
        v = 0
        if H % 2 != 0:
            for x in range(W // 2):
                if grid[H // 2][x] != grid[H // 2][W - 1 - x]:
                    ans0 += 1
                else:
                    v += grid[H // 2][x]
        if W % 2 != 0:
            for y in range(H // 2):
                if grid[y][W // 2] != grid[H - 1 - y][W // 2]:
                    ans0 += 1
                else:
                    v += grid[y][W // 2]
        ans += ans0
        if v % 2 != 0 and ans0 == 0:
            ans += 2
        if H % 2 != 0 and W % 2 != 0:
            ans += grid[H // 2][W // 2]
        return ans
