class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        for(int i=0;i<=m/2-1;i++){
            for(int j=0;j<=n/2-1;j++){
                int cnt=0;
                if(grid[i][j]==1){
                    cnt++;
                }
                if(grid[i][n-j-1]==1){
                    cnt++;
                }
                if(grid[m-i-1][j]==1){
                    cnt++;
                }
                if(grid[m-i-1][n-j-1]==1){
                    cnt++;
                }
                ans+=min(cnt,4-cnt);
            }
        }
        // cout<<ans<<'\n';
        if((m&1) && (n&1)){
            if(grid[m/2][n/2]==1){
                grid[m/2][n/2]=0;
                ans++;
            }
            // cout<<ans<<'\n';
            int cnt=0;
            int one=0;
            for(int i=0;i<=m/2-1;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]){
                    cnt++;
                }
                else{
                    one+=2*(grid[i][n/2]==1);
                }
            }
            for(int j=0;j<=n/2-1;j++){
                if(grid[m/2][j]!=grid[m/2][n-j-1]){
                    cnt++;
                }
                else{
                    one+=2*(grid[m/2][j]==1);
                }
            }
            ans+=cnt;
            // cout<<ans<<'\n';
            if(one%4!=0){
                if(cnt==0) ans+=2;
            }
            // cout<<ans<<'\n';
        }
        else if((m&1)){
            int cnt=0;
            int one=0;
            for(int j=0;j<=(n-2)/2;j++){
                if(grid[m/2][j]!=grid[m/2][n-j-1]){
                    cnt++;
                }
                else{
                    one+=2*(grid[m/2][j]==1);
                }
            }
            ans+=cnt;
            // cout<<ans<<'\n';
            if(one%4!=0){
                if(cnt==0) ans+=2;
            }
            // cout<<ans<<'\n';
        }
        else if((n&1)){
            int cnt=0;
            int one=0;
            for(int i=0;i<=(m-2)/2;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]){
                    cnt++;
                }
                else{
                    one+=2*(grid[i][n/2]==1);
                }
            }
            ans+=cnt;
            // cout<<ans<<'\n';
            if(one%4!=0){
                if(cnt==0) ans+=2;
            }
            // cout<<ans<<'\n';
        }

        return ans;
    }
};
