class Solution {
    private int helper(int[] arr) {
        if (arr[2] % 2 == 1) {
            if (arr[1] > 0) {
                return arr[1];
            } else {
                return 2;
            }
        } else {
            return arr[1];
        }
    }
    private int funCase1(int[][] grid, int n, int m) {
        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                return 1;
            }
        }
        return 0;
    }
    private void funCase2(int[][] grid, int[] shift, int n, int m) {
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int sm = grid[n / 2][a] + grid[n / 2][b];
                shift[sm] += 1;
                a += 1;
                b -= 1;
            }
        }
    }
    private void funCase3(int[][] grid, int[] shift, int n, int m) {
        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int sm = grid[a][m / 2] + grid[b][m / 2];
                shift[sm] += 1;
                a += 1;
                b -= 1;
            }
        }
    }
    private int countG(int[][] grid, int n, int m) {
        int flips = 0;
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int sm = a + b + c + d;

                if (sm != 0 && sm != 4) {
                    flips += Math.min(sm, 4 - sm);
                }
            }
        }
        return flips;
    }
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int ans = 0;

        ans += funCase1(grid, n, m);

        int[] arr = new int[3];

        funCase2(grid, arr, n, m);
        funCase3(grid, arr, n, m);

        ans += helper(arr);
        ans += countG(grid, n, m);

        return ans;
    }
}
