class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        set<pair<int,int>>st;
        int sz=0;
         int ct1=0;
                int ct0=0;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                int ct=0;
                if(grid[i][j]==1)ct++;
                if(grid[i][m-1-j]==1)ct++;
                if(grid[n-1-i][j]==1)ct++;
                if(grid[n-1-i][m-1-j]==1)ct++;
                st.insert({i,j});
                st.insert({i,m-1-j});
                st.insert({n-1-i,j});
                st.insert({n-1-i,m-1-j});
                int dif=st.size()-sz;
                sz=st.size();

                if(dif==0)continue;
                if(dif==4)
                {


                if(ct==1)ans++;
                else if(ct==2)
                {
                    ans+=2;
                }
                else if(ct==3)
                {
                    ans++;
                }

                }
                else if(dif==2)
                {
                    if(ct==2)
                    {
                        ans++;
                        ct0++;
                    }
                    else if(ct==4)ct1++;
                }
                else if(dif==1)
                {
                    if(ct==4)ans++;
                }

            }
        }
        if(ct1%2!=0)
        {
            if(ct0==0)ans+=2;
        }
        return ans;
    }
};
