class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int ans = 0;
        for(int i = 0; i < n>>1; i++){
            for(int j = 0; j < m>>1; j++){
                int a[4][2] = {{i, j}, {n-i-1, j}, {i, m-j-1}, {n-i-1, m-j-1}};
                int cnt[2] = {0};
                for(int x = 0; x < 4; x++)
                    ++cnt[g[a[x][0]][a[x][1]]];
                ans += min(cnt[0], cnt[1]);
            }
        }
        if(n&1 && m&1)
            ans += g[n>>1][m>>1];
        int cnt = 0, c2 = 0;
        if(n&1){
            int l = 0, r = m-1;
            while(l < r){
                int x=n>>1;
                if(g[x][l]^g[x][r])
                    c2++;
                if(g[x][l]&g[x][r])
                    cnt++;
                l++;
                r--;
            }
        }
        if(m&1){
            int l = 0, r =n-1;
            while(l < r){
                int x=m>>1;
                if(g[l][x]^g[r][x])
                    c2++;
                if(g[l][x]&g[r][x])
                    cnt++;
                l++;
                r--;
            }
        }
        ans += c2;
        if(cnt&1 && c2 == 0)
            ans += 2;
        return ans;
    }
};
