class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        if(m==1 && n==1){
            if(grid[0][0] == 1) return 1;
            return 0;
        }
        int ans = 0;
        for(int i = 0; i<n/2; i++){
            for(int j = 0; j<m/2; j++){
                int ones = grid[i][j];
                ones += grid[n-i-1][j];
                ones += grid[i][m-j-1];
                ones += grid[n-i-1][m-j-1];
                ans += min(ones, 4-ones);
            }
        }
        int extra = 0;
        int ans2 = 0;
        bool flag=true;
        if(m%2){
            int k = 0;
            int l = n-1;
            while(k<l){
                if(grid[k][m/2]!=grid[l][m/2]){
                    ans2+=1;
                    flag = false;
                }else if(grid[k][m/2] == grid[l][m/2] && grid[k][m/2] == 1) extra += 2;
                k++;
                l--;
            }

        }
        // cout << flag << endl;

        flag = true;
        if(n%2){
            int k = 0;
            int l = m-1;
            while(k<l){
                if(grid[n/2][k] != grid[n/2][l]){
                    ans2+=1;
                    flag= false;
                }else if(grid[n/2][k] == grid[n/2][l] && grid[n/2][k] == 1) extra +=2;
                k++;
                l--;
            }

        }
        if(extra % 4 != 0){
            if(ans2==0){
                ans2+=2;
            }
        }
        if((n%2 && m%2) && grid[n/2][m/2] == 1) ans+=1;
        return ans+ans2 ;
    }
};
