using ll =int;
class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int c1=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int tc=0;
                if(grid[i][j]==1)tc++;
                if(grid[i][m-j-1]==1)tc++;
                if(grid[n-i-1][j]==1)tc++;
                if(grid[n-i-1][m-j-1]==1)tc++;
                cout<<tc<<endl;
                if(tc==0||tc==4)continue;
                else if(tc==1||tc==3)c1++;
                else c1+=2;
            }
        }
        int bp=0,oc=0;
        if(n%2&&m%2){
            if(grid[n/2][m/2]==1)c1++;
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-i-1][m/2]){
                    bp+=2;
                }else if(grid[i][m/2]==1){
                    oc+=2;
                }
            }
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1]){
                    bp+=2;
                }else if(grid[n/2][j]==1){
                    oc+=2;
                }
            }
        }else if(n%2){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1]){
                    bp+=2;
                }else if(grid[n/2][j]==1){
                    oc+=2;
                }
            }
        }else if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-i-1][m/2]){
                    bp+=2;
                }else if(grid[i][m/2]==1){
                    oc+=2;
                }
            }
        }
        if(oc%4==0){
            c1+=bp/2;
        }else{
            if(bp==0){
                c1+=2;
            }else{
                c1+=bp/2;
            }
        }
        return c1;
    }
};
