class Solution {
    func minFlips(_ grid: [[Int]]) -> Int {
        let rows = grid.count
        let cols = grid[0].count
        var result = 0

        if rows % 2 == 1 && cols % 2 == 1 {
            if grid[rows / 2][cols / 2] == 1 {
                result += 1
            }
        }

        var flipCount = [0, 0, 0]
        if rows % 2 == 1 {
            var left = 0
            var right = cols - 1
            while left < right {
                let sum = grid[rows / 2][left] + grid[rows / 2][right]
                flipCount[sum] += 1
                left += 1
                right -= 1
            }
        }

        if cols % 2 == 1 {
            var top = 0
            var bottom = rows - 1
            while top < bottom {
                let sum = grid[top][cols / 2] + grid[bottom][cols / 2]
                flipCount[sum] += 1
                top += 1
                bottom -= 1
            }
        }

        if flipCount[2] % 2 == 1 {
            if flipCount[1] > 0 {
                result += flipCount[1]
            } else {
                result += 2
            }
        } else {
            result += flipCount[1]
        }

        for i in 0..<(rows / 2) {
            for j in 0..<(cols / 2) {
                let topLeft = grid[i][j]
                let bottomLeft = grid[rows - 1 - i][j]
                let topRight = grid[i][cols - 1 - j]
                let bottomRight = grid[rows - 1 - i][cols - 1 - j]

                let sum = topLeft + bottomLeft + topRight + bottomRight

                if sum != 0 && sum != 4 {
                    result += min(sum, 4 - sum)
                }
            }
        }

        return result
    }
}
