class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ret = 0;
        for(int i=0; i<m/2; ++i){
            for(int j=0; j<n/2; ++j){
                int all = grid[i][j] + grid[m-1-i][j] + grid[i][n-1-j] + grid[m-1-i][n-1-j];
                if(all == 0 || all == 4)
                    continue;
                else if(all == 2)
                    ret += 2;
                else
                    ret++;
            }
        }
        if(m%2 == 1 && n%2 == 1){
//            cout<<"hello"<<endl;
            if(grid[m/2][n/2])
                ret++;
            int cnt = 0;
            int one = 0;
            for(int i=0; i<m/2; ++i){
                one += grid[i][n/2] + grid[m-1-i][n/2];
                cnt += grid[i][n/2] != grid[m-1-i][n/2];
            }
            for(int i=0; i<n/2; ++i){
                one += grid[m/2][i] + grid[m/2][n-i-1];
                cnt += grid[m/2][i] != grid[m/2][n-i-1];
            }
            if( (one+cnt)%4==0 || (one-cnt)%4==0 || (one%4==0 && cnt%2==0) || cnt >= 2)
                ret += cnt;
            else
                ret += cnt + 2;            
        }
        else if(m%2 == 1){
//            cout<<"hello 2"<<endl;
            vector<int> arr = grid[m/2];
            int cnt = 0;
            int s = arr.size();
            int one = 0;
            for(int i=0; i<s/2; ++i){
                one += arr[i] + arr[s-1-i];
                cnt += (arr[i] != arr[s-1-i]);
            }
            if( (one+cnt)%4==0 || (one-cnt)%4==0 || (one%4==0 && cnt%2==0) || cnt >= 2)
                ret += cnt;
            else
                ret += cnt + 2;
        }
        else if(n%2 == 1){
//            cout<<"hello 3"<<endl;
            vector<int> arr;
            for(int i=0; i<m; ++i)
                arr.push_back(grid[i][n/2]);
            int cnt = 0;
            int s = arr.size();
            int one = 0;
            for(int i=0; i<s/2; ++i){
                one += arr[i] + arr[s-1-i];
                cnt += (arr[i] != arr[s-1-i]);
            }
//            cout<<one<<" "<<cnt<<" output"<<endl;
            if( (one+cnt)%4==0 || (one-cnt)%4==0 || (one%4==0 && cnt%2==0) || cnt >= 2)
                ret += cnt;
            else
                ret += cnt + 2;
        }
        return ret;
    }
};
