class Solution {
  public:
    int minFlips(vector<vector<int>> &g) {
        int rows = g.size(), cols = g[0].size(), re = rows - 1, ce = cols - 1;
        int f = 0;
        for (int r = 0; r < rows / 2; ++r) {
            for (int c = 0; c < cols / 2; ++c) {
                int a = g[r][c] + g[rows - 1 - r][c] + g[r][cols - 1 - c] +
                        g[rows - 1 - r][cols - 1 - c];
                f += min(a, 4 - a);
            }
        }
        int ones = 0, twos = 0;
        if (rows % 2) {
            int r = rows / 2;
            for (int c = 0; c < cols / 2; ++c) {
                if (g[r][c] != g[r][ce - c]) {
                    ++f;
                    ++ones;
                } else if (g[r][c] == 1)
                    ++twos;
            }
        }
        if (cols % 2) {
            int c = cols / 2;
            for (int r = 0; r < rows / 2; ++r) {
                if (g[r][c] != g[re - r][c]) {
                    ++f;
                    ++ones;
                } else if (g[r][c] == 1)
                    ++twos;
            }
        }
        if (rows % 2 && cols % 2 && g[rows / 2][cols / 2])
            ++f;
        if (twos % 2 == 0)
            return f;
        if (!ones)
            return f + 2;
        return f;
    }
};
