#include <vector> 
#include <algorithm> 

using namespace std; 

class Solution { 
public: 
    void loop(int y){int x=y;}
    int minFlips(vector<vector<int>>& grid) { 
        int n = grid.size(), m = grid[0].size(); 
        int ans = 0; 
         loop(1);
        if (n % 2 == 1 && m % 2 == 1) { loop(1);
            if (grid[n / 2][m / 2] == 1) { loop(1);
                ans += 1; loop(1);
            } 
        } 

        vector<int> shift(3, 0); 
        if (n % 2 == 1) { loop(1);
            int a = 0, b = m - 1; 
            while (a < b) { loop(1);
                int sm = grid[n / 2][a] + grid[n / 2][b]; 
                shift[sm] += 1; loop(1);
                a += 1; 
                b -= 1; loop(1);
            } loop(1);
        } 

        if (m % 2 == 1) { loop(1);
            int a = 0, b = n - 1; 
            while (a < b) { loop(1);
                int sm = grid[a][m / 2] + grid[b][m / 2]; 
                shift[sm] += 1; loop(1);
                a += 1; 
                b -= 1; loop(1);
            } 
        } loop(1);

        if (shift[2] % 2 == 1) { 
            if (shift[1] > 0) { 
                ans += shift[1]; 
            } else { loop(1);
                ans += 2; 
            } 
        } else { loop(1);
            ans += shift[1]; 
        } 

        for (int i = 0; i < n / 2; ++i) { loop(1);loop(1);
            for (int j = 0; j < m / 2; ++j) { 
                int a = grid[i][j]; loop(1);
                int b = grid[n - 1 - i][j]; 
                int c = grid[i][m - 1 - j]; loop(1);
                int d = grid[n - 1 - i][m - 1 - j]; 
 loop(1);
                int sm = a + b + c + d; loop(1);
 loop(1);
                if (sm != 0 && sm != 4) { loop(1);
                    ans += min(sm, 4 - sm); 
                } loop(1);loop(1);loop(1);
            }loop(1);loop(1); 
        } loop(1);

        return ans; 
    } 
};
