class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        lis = [0,float("inf"),float("inf"),float("inf")]
        m, n = len(grid), len(grid[0])
        for i in range(m//2):
            for j in range(n//2):
                a, b, c, d = grid[i][j], grid[i][-j-1], grid[-1-i][-1-j], grid[-1-i][j]
                t, s = (a!=0) + (b!=0) + (c!=0) + (d!=0), (a!=1) + (b!=1) + (c!=1) + (d!=1)
                a,b,c,d = lis
                x = min(s,t)
                lis = [a+x, b+x, c+x, d+x]
            # print(lis)
        if n % 2:
            x = n // 2
            for i in range(m//2):
                a, b = grid[i][x], grid[-1-i][x]
                t, s = (a!=0) + (b!=0), (a!=1) + (b!=1)
                a,b,c,d = lis
                lis = [min(a+t,c+s), min(b+t,d+s), min(c+t,a+s), min(d+t,b+s)]
            # print(lis)
        if m % 2:
            x = m // 2
            for j in range(n//2):
                a, b = grid[x][j], grid[x][-j-1]
                t, s = (a!=0) + (b!=0), (a!=1) + (b!=1)
                a,b,c,d = lis
                lis = [min(a+t,c+s), min(b+t,d+s), min(c+t,a+s), min(d+t,b+s)]
                # print(lis)
            if n % 2:
                a,b,c,d = lis
                if grid[m//2][n//2]:
                    lis = [min(d,a+1), min(a,b+1), min(b,c+1), min(c,d+1)]
                else:
                    lis = [min(a,d+1), min(b,a+1), min(c,b+1), min(d,c+1)]
                # print(lis)


        return lis[0]
