class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ans = 0
        r, c = len(grid), len(grid[0])
        for i in range(r // 2):
            for j in range(c // 2):
                print(i, j)
                s = grid[i][j] + grid[i][-j - 1] + grid[-i - 1][j] + grid[-i - 1][-j - 1]
                ans += min(s, (4 - s))
        count1 = 0
        changed = 0
        if r % 2 != 0:
            for i in range(c // 2):
                a, b = r // 2, i
                s = grid[a][b] + grid[a][-b-1]
                if s == 2:
                    count1 += 2
                elif s == 1:
                    changed += 1
                    ans += 1
        if c % 2 != 0:
            for i in range(r // 2):
                a, b = i, c // 2
                s = grid[a][b] + grid[-a-1][b]
                if s == 2:
                    count1 += 2
                elif s == 1:
                    changed += 1
                    ans += 1
        if r % 2 != 0 and c % 2 != 0:
            if grid[r // 2][c // 2] == 1:ans += 1
        count1 = count1 % 4
        if count1 == 2:
            if changed > 0:
                return ans
            else:
                return ans + 2
        return ans
