class Solution {
        public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int ans = 0;
        int to = 0;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int x1 = grid[i][j];
                int x2 = grid[i][m - j - 1];
                int x3 = grid[n - i - 1][j];
                int x4 = grid[n - i - 1][m - j - 1];

                int ones = x1 + x2 + x3 + x4;
                to += ones;
                ans += Math.min(ones, 4 - ones);
            }
        }
        if (m % 2 == 1 && n % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }
	int flips = 0;
          int ones = 0;
        if (n % 2 == 1) {
            for (int i = 0; i < m / 2; i++) {
                if (grid[n / 2][i] == grid[n / 2][m - i - 1] && grid[n / 2][i] == 1) {
                    ones += 1;
                } else if (grid[n / 2][i] != grid[n / 2][m - i - 1]) {
                    flips += 1;
                }
            }
        }
        if (m % 2 == 1) {
            for (int i = 0; i < n / 2; i++) {
                if (grid[i][m / 2] == grid[n - i - 1][m / 2] && grid[i][m / 2] == 1) {
                    ones += 1;
                } else if (grid[i][m / 2] != grid[n - i - 1][m / 2]) {
                    flips += 1;
                }
            }

        }
	if (ones % 2 == 0) {
                ans += flips;
            } else {
                if (flips > 0) {
                    ans += flips;
                } else {
                    ans += 2;
                }
            }

        return ans;
    }


}
