class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int res = 0;
        int n = grid.size();
        int m = grid[0].size();
        if (n % 2 != 0 && m % 2 != 0) {
            int n10 = 0;
            int n11 = 0;
            int n00 = 0;
            for (int i = 0; i < m / 2; i++) {
                if (grid[n / 2][i] + grid[n / 2][m - i - 1] == 2) {
                    n11++;
                }
                if (grid[n / 2][i] + grid[n / 2][m - i - 1] == 0) {
                    n00++;
                }
                if (grid[n / 2][i] + grid[n / 2][m - i - 1] == 1) {
                    n10++;
                }
            }
            for (int i = 0; i < n / 2; i++) {
                if (grid[i][m / 2] + grid[n - i - 1][m / 2] == 2) {
                    n11++;
                }
                if (grid[i][m / 2] + grid[n - i - 1][m / 2] == 0) {
                    n00++;
                }
                if (grid[i][m / 2] + grid[n - i - 1][m / 2] == 1) {
                    n10++;
                }
            }
            cout << n00 << " " << n10 << " " << n11 << endl;
            if (n11 % 2 == 1 && n10 < 1) {
                res += 2;
            }
            res += n10;
            res += grid[n / 2][m / 2];
        } else if (n % 2 != 0 && m % 2 == 0) {
            int n10 = 0;
            int n11 = 0;
            int n00 = 0;
            for (int i = 0; i < m / 2; i++) {
                if (grid[n / 2][i] + grid[n / 2][m - i - 1] == 2) {
                    n11++;
                }
                if (grid[n / 2][i] + grid[n / 2][m - i - 1] == 0) {
                    n00++;
                }
                if (grid[n / 2][i] + grid[n / 2][m - i - 1] == 1) {
                    n10++;
                }
            }
            cout << n00 << " " << n10 << " " << n11 << endl;
            if (n11 % 2 == 1 && n10 < 1) {
                res += 2;
            }
            res += n10;
        } else if (n % 2 == 0 && m % 2 != 0) {
            int n10 = 0;
            int n11 = 0;
            int n00 = 0;
            for (int i = 0; i < n / 2; i++) {
                if (grid[i][m / 2] + grid[n - i - 1][m / 2] == 2) {
                    n11++;
                }
                if (grid[i][m / 2] + grid[n - i - 1][m / 2] == 0) {
                    n00++;
                }
                if (grid[i][m / 2] + grid[n - i - 1][m / 2] == 1) {
                    n10++;
                }
            }
            cout << n00 << " " << n10 << " " << n11 << endl;
            if (n11 % 2 == 1 && n10 < 1) {
                res += 2;
            }
            res += n10;
        }
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int tmp = grid[i][j] + grid[i][m - j - 1] + grid[n - i - 1][j] + grid[n - i - 1][m - j - 1];
                if (tmp == 1) {
                    res += 1;
                } else if (tmp == 2) {
                    res += 2;
                } else if (tmp == 3) {
                    res += 1;
                }
            }
        }
        return res;
    }
};
