class Solution {
    public int minFlips(int[][] g) {
        int n = g.length;
        int m = g[0].length;

        int res = 0;
        res += ab(g, n, m);

        int[] mid = cd(g, n, m);
        res += mid[0];

        if (mid[1] == 0 && mid[2] % 2 != 0) {
            res += 2;
        }

        return res;
    }

    private int ab(int[][] g, int n, int m) {
        int flips = 0;    
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int[] elems = {
                    g[i][j],
                    g[n - i - 1][j],
                    g[i][m - j - 1],
                    g[n - i - 1][m - j - 1]
                };

                int ones = cd(elems);

                flips += Math.min(ones, 4 - ones);
            }
        }

        return flips;
    }

    private int cd(int[] elems) {
        int count = 0;
        for (int num : elems) {
            count += num;
        }
        return count;
    }

    private int[] cd(int[][] g, int n, int m) {
        int[] res = new int[3];

        if (n % 2 != 0) {
            res = ef(g, n, m, res);
        }

        if (m % 2 != 0) {
            res = gh(g, n, m, res);
        }

        if (n % 2 != 0 && m % 2 != 0 && g[n / 2][m / 2] == 1) {
            res[0]++;
        }

        return res;
    }

    private int[] ef(int[][] g, int n, int m, int[] res) {
        for (int j = 0; j < m / 2; j++) {
            if (g[n / 2][j] != g[n / 2][m - j - 1]) {
                res[0]++;
                res[1]++;
            } else {
                if (g[n / 2][j] == 1) {
                    res[2]++;
                }
            }
        }
        return res;
    }

    private int[] gh(int[][] g, int n, int m, int[] res) {
        for (int i = 0; i < n / 2; i++) {
            if (g[i][m / 2] != g[n - i - 1][m / 2]) {
                res[0]++;
                res[1]++;
            } else {
                if (g[i][m / 2] == 1) {
                    res[2]++;
                }
            }
        }
        return res;
    }
}
