class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        const int n = grid.size(), m = grid[0].size();
        int ans = 0;
        array<int, 3> counts {};
        for (int t = 0, b = n - 1; t < b; ++t, --b) {
            for (int l = 0, r = m - 1; l < r; ++l, --r) {
                const int ones = grid[t][l] + grid[t][r] + grid[b][l] + grid[b][r];
                ans += min(ones, 4 - ones);
            }
            if (m & 1) {
                ++counts[grid[t][m / 2] + grid[b][m / 2]];
            }
        }
        if (n & 1) {
            for (int l = 0, r = m - 1; l < r; ++l, --r) {
                ++counts[grid[n / 2][l] + grid[n / 2][r]];
            }
            if (m & 1) {
                if (grid[n / 2][m / 2]) {
                    ++ans;
                }
            }
        }
        counts[2] %= 2;
        ans += counts[1];
        if (counts[2] && !counts[1]) {
            ans += 2;
        }
        return ans;
    }
};
