class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int t = 0, d = m-1;
        int ans = 0;
        while(t < d){
            int l = 0, r = n-1;
            while(l < r){
                vector<int> v(2,0);
                v[grid[t][l]]++;
                v[grid[t][r]]++;
                v[grid[d][l]]++;
                v[grid[d][r]]++;
                ans += min(v[0], v[1]);
                l++;
                r--;
            }
            t++; 
            d--;
        }
        int one = 0;
        int diff = 0;
        if(m % 2 != 0){
            int l = 0, r = n-1;
            int i = m/2;
            while(l <= r){
                if(l == r && grid[i][l] == 1) ans++;
                else if(grid[i][l] != grid[i][r]) diff++;
                else if(grid[i][l] == grid[i][r] && grid[i][l] == 1) one+=2;
                l++;
                r--;
            }
        }
        if(n % 2 != 0){
         int l = 0, r = m-1;
         int i = n/2;
         while( l < r){
            if(grid[l][i] != grid[r][i]) diff++;
            else if(grid[l][i] == grid[r][i] && grid[l][i] == 1) one+=2;
            l++;
            r--;
         }
        }
        one = one % 4;
        if(one == 2 && diff > 0) ans+= diff;
        else if(one == 2 && diff == 0) ans+=one;
        else ans += diff;
        return ans;

    }
};
