class Solution {
    int m, n;
    public int minFlips(int[][] grid) {
        m = grid.length;
        n = grid[0].length;
        int rowBeg = 0, rowEnd = m - 1;
        int colBeg = 0, colEnd = n - 1;
        boolean f = false;
        int flips = 0;
        while(rowBeg < rowEnd){
            colBeg = 0;
            colEnd = n - 1;
            while(colBeg < colEnd){
                int ones = 0;
                if(grid[rowBeg][colBeg] == 1) ones++;
                if(grid[rowBeg][colEnd] == 1) ones++;
                if(grid[rowEnd][colBeg] == 1) ones++;
                if(grid[rowEnd][colEnd] == 1) ones++;
                flips += Math.min(ones, 4 - ones);
                colBeg++;
                colEnd--;
            }
            rowBeg++;
            rowEnd--;
        }
        int sameOnes = 0;
        int curFlips = 0;
        if(m % 2 == 1){
            int row = m / 2;
            colBeg = 0;
            colEnd = n - 1;
            while(colBeg < colEnd){
                if(grid[row][colBeg] != grid[row][colEnd]) curFlips++;
                if(grid[row][colBeg] == 1 && grid[row][colEnd] == 1) sameOnes += 2;
                colBeg++;
                colEnd--;
            }
            if(colBeg == colEnd && grid[row][colBeg] == 1){
                flips++;
                f = true;
            } 
        }
        if(n % 2 == 1){
            int col = n / 2;
            rowBeg = 0;
            rowEnd = m - 1;
            while(rowBeg < rowEnd){
                if(grid[rowBeg][col] != grid[rowEnd][col]) curFlips++;
                if(grid[rowBeg][col] == 1 && grid[rowEnd][col] == 1) sameOnes += 2;
                rowBeg++;
                rowEnd--;
            }
            if(rowBeg == rowEnd && grid[rowEnd][col] == 1 && !f) flips++;
        }
        if(sameOnes % 4 != 0){
            if(curFlips > 0) flips += curFlips;
            else flips += 2;
        }
        else flips += curFlips;
        return flips;
    }
}
