class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int res = 0;
        for (int i=0;i<n/2;i++){
            for (int j=0;j<m/2;j++){
                int temp = 0;
                temp += grid[n-i-1][j];
                temp += grid[n-i-1][m-j-1];
                temp += grid[i][m-j-1];
                temp += grid[i][j];
                res += Math.min(temp, 4-temp);
            }
        }
        int flip = 0, count = 0;

        if (n %2 == 1){
            for (int j=0;j<m/2;j++){
                if (grid[n/2][j] != grid[n/2][m-j-1]){
                    res++;
                    flip++;
                } else {
                    if (grid[n/2][j] == 1) count++;
                }
            }
        }

        if (m %2 == 1){
            for (int i=0;i<n/2;i++){
                if (grid[i][m/2] != grid[n-i-1][m/2]) {
                    res++;
                    flip++;
                } else {
                    if (grid[i][m/2] == 1) count++;
                }
            }
        }

        if (n % 2 ==1 && m %2 ==1 && grid[n/2][m/2] == 1) res++;

        if (flip == 0 && count % 2 == 1) res += 2;

        return res;
    }
}
