class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0
        for i in range(m // 2):
            for j in range(n // 2):
                a = [grid[i][j], grid[-i - 1][j], grid[i][-j - 1], grid[-i - 1][-j - 1]]
                ans += min(sum(a), 4 - sum(a))
        if m % 2 == 1 and n % 2 == 1 and grid[m // 2][n // 2] == 1:
            ans += 1
            grid[m // 2][n // 2] = 0
        ltf = []
        if m % 2 == 1:
            ltf.append(grid[m // 2])
        if n % 2 == 1:
            ltf.append([grid[i][n // 2] for i in range(m)])
        f = 0
        for line in ltf:
            for i in range(len(line) // 2):
                if line[i] != line[-i - 1]:
                    f += 1
        ans += f
        if f == 0:
            s = 0
            for line in ltf:
                s += sum(line)
            if s % 4 != 0:
                ans += 2
        return ans
