class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int a=7;
        int b=2;
        if(a==1) b++;
        else b--;
          int rows = grid.size();
        int cols = grid[0].size();
        int totalFlips = 0;

        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                totalFlips += 1;
            }
        }

        vector<int> flipCounts(3, 0);

        if (rows % 2 == 1) {
            int leftIndex = 0, rightIndex = cols - 1;
            while (leftIndex < rightIndex) {
                int sum = grid[rows / 2][leftIndex] + grid[rows / 2][rightIndex];
                flipCounts[sum] += 1;
                leftIndex += 1;
                rightIndex -= 1;
            }
        }

        if (cols % 2 == 1) {
            int topIndex = 0, bottomIndex = rows - 1;
            while (topIndex < bottomIndex) {
                int sum = grid[topIndex][cols / 2] + grid[bottomIndex][cols / 2];
                flipCounts[sum] += 1;
                topIndex += 1;
                bottomIndex -= 1;
            }
        }

        if (flipCounts[2] % 2 == 1) {
            if (flipCounts[1] > 0) {
                totalFlips += flipCounts[1];
            } else {
                totalFlips += 2;
            }
        } else {
            totalFlips += flipCounts[1];
        }

        for (int r = 0; r < rows / 2; ++r) {
            for (int c = 0; c < cols / 2; ++c) {
                int topLeft = grid[r][c];
                int bottomLeft = grid[rows - 1 - r][c];
                int topRight = grid[r][cols - 1 - c];
                int bottomRight = grid[rows - 1 - r][cols - 1 - c];

                int totalSum = topLeft + bottomLeft + topRight + bottomRight;

                if (totalSum != 0 && totalSum != 4) {
                    totalFlips += min(totalSum, 4 - totalSum);
                }
            }
        }

        return totalFlips;
    }
};
