class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int ans = 0;
        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                int x = m - 1 - i, y = n - 1 - j;
                int sum = grid[i][j] + grid[x][j] + grid[i][y] + grid[x][y];
                grid[i][j] = grid[x][j] = grid[i][y] = grid[x][y] = 0;
                ans += Math.min(sum, 4 - sum);
            }
        }
        if (m % 2 == 1 && n % 2 == 1) {
            ans += grid[m/2][n/2];
            grid[m/2][n/2] = 0;
        }
        int one = 0;
        int r = 0, c = 0;
        if (m % 2 == 1) {
            for (int j1 = 0, j2 = n - 1; j1 < j2; j1++, j2--) {
                r += grid[m/2][j1] != grid[m/2][j2] ? 1 : 0;
                if (grid[m/2][j1] == grid[m/2][j2] && grid[m/2][j1] == 1) {
                    one += 2;
                }
            }
        }
        if (n % 2 == 1) {
            for (int i1 = 0, i2 = m - 1; i1 < i2; i1++, i2--) {
                c += grid[i1][n/2] != grid[i2][n/2] ? 1 : 0;
                if (grid[i1][n/2] == grid[i2][n/2] && grid[i2][n/2] == 1) {
                    one += 2;
                }
            }
        }
        one %= 4;
        if (r == 0 && c == 0)
            return ans + one;
        else
            return ans + r + c;
    }
}
