class Solution {
    public int minFlips(int[][] grid) {
        int ans = 0;
        int a = grid.length, b = grid[0].length;
        long ones = 0;

        int par = 0, od = 0;

        for(int i = 0; i < a / 2; i++){
            for(int j = 0; j < b / 2; j++){
                int o = 0, n = 0;
                if(grid[i][j] == 1) o++;
                else n++;

                if(grid[a - i - 1][j] == 1) o++;
                else n++;

                if(grid[i][b - j - 1] == 1) o++;
                else n++;

                if(grid[a - i - 1][b - j - 1] == 1) o++;
                else n++;

                if(o == 4 || n == 4) continue;

                if(o == 2) {
                    //par++;
                    ans += 2;
                } else {
                    ans++;
                }

            }
        }

        if(a % 2 == 1){
            int k = a / 2;
            for(int i  = 0; i < b / 2; i++){
                if(grid[k][i] != grid[k][b - i - 1]){
                    od++;
                    ans++;
                    ones += 2;
                } else if (grid[k][i] == 1){
                    ones += 2;
                }
            }
        }

        if(b % 2 == 1){
            int k = b / 2;
            for(int i  = 0; i < a / 2; i++){
                if(grid[i][k] != grid[a - i - 1][k]){
                    od++;
                    ans++;
                    ones += 2;
                } else if (grid[i][k] == 1){
                    ones += 2;
                }
            }
        }

        if(a % 2 == 1 && b % 2 == 1){
            if(grid[a / 2][b / 2] == 1){
                ans++;
            }
        }


        if(ones % 4 == 0) return ans;
        else {
            if(od > 0) return ans;
            else return ans + 2;
        }
    }
}
