class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size();
        int m = g[0].size();
        int dp[4][2];
        memset(dp, 0x3f, sizeof dp);
        dp[0][0] = 0;
        for(int i=0;i<(n+1)/2;i++){
            for(int j=0;j<(m+1)/2;j++){
                int w, c;
                if(n-i-1 != i && m-j-1!=j){
                    c = g[i][j] + g[i][m-1-j] + g[n-1-i][j] + g[n-1-i][m-1-j];
                    w = 4;

                }else if(n-1-i==i&&m-1-j==j){
                    // continue;
                    w = 1;
                    c = g[i][j];
                }else if(n-1-i!=i){
                    c = g[i][j]+g[n-1-i][j];
                    // m[2][min(c, 2-c)]++;
                    // s+=min(c,2-c);
                    w = 2;
                }else{
                    c = g[i][j]+g[i][m-1-j];
                    // m[2][min(c, 2-c)]++;
                    // s+=min(c,2-c);
                    w = 2;
                }
                // cout<<w<<" "<<c<<endl;
                for(int k=0;k<4;k++){
                    dp[k][1] = min(dp[(k-w+4)%4][0]+w-c, dp[k][0]+c);
                    // cout<<dp[k][1]<<" ";
                }               
                // cout<<endl;
                for(int k=0;k<4;k++){
                    dp[k][0] = dp[k][1];
                }
            }
        }
        return dp[0][0];

    }
};
