class Solution {
public:
    int minFlips(vector<vector<int>>& mat_grid_matrix) {
        int n = mat_grid_matrix.size(), m = mat_grid_matrix[0].size();
        int res_find = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (mat_grid_matrix[n / 2][m / 2] == 1) {
                res_find += 1;
            }
        }

        vector<int> adfttvg(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int aryannnnn = mat_grid_matrix[n / 2][a] + mat_grid_matrix[n / 2][b];
                adfttvg[aryannnnn] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int ijk = 0, teuehe = n - 1;
            while (ijk < teuehe) {
                int aryannnnn = mat_grid_matrix[ijk][m / 2] + mat_grid_matrix[teuehe][m / 2];
                adfttvg[aryannnnn] += 1;
                ijk += 1;
                teuehe -= 1;
            }
        }

        if (adfttvg[2] % 2 == 1) {
            if (adfttvg[1] > 0) {
                res_find += adfttvg[1];
            } else {
                res_find += 2;
            }
        } else {
            res_find += adfttvg[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int ijk = mat_grid_matrix[i][j];
                int teuehe = mat_grid_matrix[n - 1 - i][j];
                int c = mat_grid_matrix[i][m - 1 - j];
                int d = mat_grid_matrix[n - 1 - i][m - 1 - j];

                int aryannnnn = ijk + teuehe + c + d;

                if (aryannnnn != 0 && aryannnnn != 4) {
                    res_find += min(aryannnnn, 4 - aryannnnn);
                }
            }
        }

        return res_find ;
    }
};
