class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int m = grid.size();
        int n = grid[0].size();

        for (int i = 0; i < (m / 2); i++) {
            for (int j = 0; j < (n / 2); j++) {
                int tl = grid[i][j];
                int tr = grid[i][n - 1 - j];
                int bl = grid[m - 1 - i][j];
                int br = grid[m - 1 - i][n - 1 - j];

                int sum = tl + tr + bl + br;
                if (4 - sum < sum) {
                    if (tl == 0) {
                        grid[i][j] = 1;
                        ans++;
                    }
                    if (tr == 0) {
                        grid[i][n - 1 - j] = 1;
                        ans++;
                    }
                    if (bl == 0) {
                        grid[m - 1 - i][j] = 1;
                        ans++;
                    }
                    if (br == 0) {
                        grid[m - 1 - i][n - 1 - j] = 1;
                        ans++;
                    }
                }
                else {
                    if (tl == 1) {
                        grid[i][j] = 0;
                        ans++;
                    }
                    if (tr == 1) {
                        grid[i][n - 1 - j] = 0;
                        ans++;
                    }
                    if (bl == 1) {
                        grid[m - 1 - i][j] = 0;
                        ans++;
                    }
                    if (br == 1) {
                        grid[m - 1 - i][n - 1 - j] = 0;
                        ans++;
                    }
                }
            }
        }

        if (n % 2 == 1 && m % 2 == 0) {
            int ones = 0;
            int changes = 0;
            for (int t = 0, b = m - 1; t < b; t++, b--) {
                if (grid[t][n / 2] != grid[b][n / 2]) {
                    changes++;
                    continue;
                }

                if (grid[t][n / 2] == 1) ones++;
            }

            if (changes == 0) {
                if (ones % 2 == 1) {
                    ans += 2;
                }
            }
            else {
                ans += changes;
            }
        }

        if (m % 2 == 1 && n % 2 == 0) {
            int ones = 0;
            int changes = 0;
            for (int l = 0, r = n - 1; l < r; l++, r--) {

                if (grid[m / 2][l] != grid[m / 2][r]) {
                    changes++;
                    continue;
                }

                if (grid[m / 2][l] == 1) ones++;
            }

            if (changes == 0) {
                if (ones % 2 == 1) {
                    ans += 2;
                }
            }
            else {
                ans += changes;
            }
        }

        if (m % 2 == 1 && n % 2 == 1) {
            if (grid[m / 2][n / 2] == 1) {
                ans++;
            }

            int ones_i = 0;
            int changes_i = 0;
            for (int t = 0, b = m - 1; t < b; t++, b--) {
                if (grid[t][n / 2] != grid[b][n / 2]) {
                    changes_i++;
                    continue;
                }

                if (grid[t][n / 2] == 1) ones_i++;
            }

            int ones_j = 0;
            int changes_j = 0;
            for (int l = 0, r = n - 1; l < r; l++, r--) {
                if (grid[m / 2][l] != grid[m / 2][r]) {
                    changes_j++;
                    continue;
                }

                if (grid[m / 2][l] == 1) ones_j++;
            }

            if ((ones_i + ones_j) % 2 == 0) {
                ans += changes_i + changes_j;
            }
            else {
                if (changes_i != 0 && changes_j == 0) {
                    ans += changes_i;
                }
                else if (changes_j != 0 && changes_i == 0) {
                    ans += changes_j;
                }
                else if (changes_i != 0 && changes_j != 0) {
                    ans += changes_i + changes_j;
                }
                else {
                    ans += 2;
                }
            }
        }

        return ans;
    }
};

