class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n=len(grid)
        m=len(grid[0])

        cnt=0
        extraOnes=0
        changeOnSame=0
        answer=0
        for i in range((n+1)//2):
            for j in range((m+1)//2):
                cnt0=0
                cnt1=0
                cnt0+=(grid[i][j]==0)
                cnt0+=(grid[i][m-j-1]==0)
                cnt0+=(grid[n-i-1][j]==0)
                cnt0+=(grid[n-i-1][m-j-1]==0)
                cnt1+=(grid[i][j]==1)
                cnt1+=(grid[i][m-j-1]==1)
                cnt1+=(grid[n-i-1][j]==1)
                cnt1+=(grid[n-i-1][m-j-1]==1)

                if i==n-i-1 and j==m-j-1:
                    continue
                if i==n-i-1 or j==m-j-1:
                    if cnt0==cnt1:
                        changeOnSame+=1
                        answer+=1
                    else:
                        if cnt1==4:
                            extraOnes+=2
                else:
                    answer+=min(cnt0,cnt1)

        if extraOnes%4==2 and changeOnSame==0:
            answer+=2

        if n%2==1 and m%2==1 and grid[n//2][m//2]==1:
            answer+=1

        return answer
